import React from 'react';

function ColorBoxes({ boxes }) {
  return (
    <div className="boxes">
      {boxes.map((color, idx) => (
        <div key={idx} className="box" style={{ backgroundColor: color }}></div>
      ))}
    </div>
  );
}

export default ColorBoxes;


