import React, { useState } from 'react';
import EightBall from './components/EightBall';
import ColorBoxes from './components/ColorBoxes';
import './App.css';

function App() {
  const answers = [ /* your answers array */ ];
  const defaultColors = ["red", "green", "blue", "yellow", "purple"];
  const [color, setColor] = useState("black");
  const [message, setMessage] = useState("Think of a Question");
  const [boxes, setBoxes] = useState(Array.from({ length: 16 }, () => defaultColors[Math.floor(Math.random() * defaultColors.length)]));

  const handleClick = () => {
    let randomIndex = Math.floor(Math.random() * answers.length);
    if (answers[randomIndex]) {
      setMessage(answers[randomIndex].msg);
      setColor(answers[randomIndex].color);
      setBoxes(boxes.map(() => defaultColors[Math.floor(Math.random() * defaultColors.length)]));
    } else {
      console.error(`No answer at index ${randomIndex}`);
    }
  };

  return (
    <div className="App">
      <EightBall color={color} message={message} handleClick={handleClick} />
      <ColorBoxes boxes={boxes} />
    </div>
  );
}

export default App;


