import React from 'react';

function EightBall({ color, message, handleClick }) {
  return (
    <div className="eight-ball" style={{ backgroundColor: color }}>
      <p>{message}</p>
      <button onClick={handleClick}>Change</button>
    </div>
  );
}

export default EightBall;



